import React from 'react'
import { ListGroup } from 'reactstrap'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Home from './home';
import AllCourses from './allCourses';
import addCourse from './addCourse';

function Menu(){
    return (
      <div className="">
       <nav className="w-full margin-auto  ">
       <Router>
       
          <ul className="flex flex-col justify-center py-2 md:flex-row items-center text-white  bg-gray-700">
            <li className='text-xl py-3 mx-4 hover:scale-105'>
            <Link to="/">Home</Link></li>
            <li className='text-xl py-3 mx-4 hover:scale-105'>
            <Link to="/add-course">View Courses</Link></li>
            <li className='text-xl py-3 mx-4 hover:scale-105'>
            <Link to="/view-courses">View Courses</Link></li>
            <li className='text-xl py-3 mx-4 hover:scale-105'>Contact Us</li>
            <li className='text-xl py-3 mx-4 hover:scale-105'>About Us</li>
          </ul>
         
          <Route path='/' component={Home} exact/>
          <Route path='/add-course' component={addCourse} exact/>
          <Route path='/view-courses' component={AllCourses} exact/>
          </Router>
        </nav>
      </div>
    ); 
}

export default Menu;
