import React, { useEffect, useState } from 'react'
import Course from './course'
import axios from 'axios'
import base_path from '../api/bootapi'
import  toast  from 'reactstrap'

function AllCourses() {
  useEffect(() => {
    document.title = "All courses || Code Master";
  }, []);

  const getAllCoursesFromServer = () => {
    axios.get(`${base_path}/courses`).then(
      (response) => {
        console.log(response.data);
        toast.success("Success")
      },
      (error) => {
        console.log(error);
      }
    );
  };

  useEffect(() => {
    getAllCoursesFromServer()
  },[]);

  const [course, setCourse] = useState([
    {
      title: "Java for Beginners",
      description:
        "This is an updated Java course for beginners with all the fundamental concepts",
    },
    {
      title: "React for Beginners",
      description:
        "This is an updated React course for beginners with all the fundamental javaScript concepts",
    },
    {
      title: "Django for All",
      description:
        "This is an updated Django course for beginners with all the fundamental concepts",
    },
  ]);

  return (
    <div className="text-center" style={{ width: "90%", marginLeft: "5%" }}>
      <h1 style={{ color: "white" }}>All Courses</h1>
      <p style={{ color: "white" }}>
        List of all the courses available at affordable price
      </p>
      {course.length > 0
        ? course.map((item) => (
            <div key={item.id} style={{ marginBottom: "10px" }}>
              <Course course={item} />
            </div>
          ))
        : "No course available"}
    </div>
  );
}

export default AllCourses;
