import React, { Fragment, useEffect, useState } from 'react'
import { Form, FormGroup,Input, FormText, Button, Container } from 'reactstrap';
import Course from './course';
import axios from 'axios';
import base_path from '../api/bootapi';
import { toast } from 'react-toastify';

const AddCourse=()=>{

  useEffect(()=>{
    document.title="Add Course || Code Master";
  },[]);

  const [course, setCourse]=useState({});

  //form handler
  const handleForm=(e)=>{
    console.log(course);
    postData(course);
    e.preventDefault();
  }

  // function to post data on server

  const postData=(data)=>{
          axios.post(`${base_path}/courses`,data).then(
            (response)=>{
               console.log(response);
               toast.success("Course Added")
            },
            (error)=>{
                 console.log(error);
                 toast.error("Something went wrong")

            }
          )
  }

    return (
      <Fragment >
        <h1 className="text-center my-3" style={{ color: "white" }}>
          Fill Course Details
        </h1>
        <Form onSubmit={handleForm} style={{width: "60%", justifyContent:"center", marginLeft: "20%"}}>
          <FormGroup >
            <label for="userId" style={{ color: "white" }}>
              Course Id
            </label>
            <Input
              type="text"
              placeholder="Enter Here"
              name="userId"
              id="userId"
              onChange={(e)=>{setCourse({...course,id: e.target.value});}}
            ></Input>
          </FormGroup>
          <FormGroup>
            <label for="userName" style={{color:'white'}}>Course Name</label>
            <Input
              type="text"
              placeholder="Enter Here"
              name="userName"
              id="title"
              onChange={(e)=>{setCourse({...course,title: e.target.value})}}
            ></Input>
          </FormGroup>
          <FormGroup>
            <label for="description" style={{color:'white'}}>Course Description</label>
            <Input
              type="textarea"
              placeholder="Enter Description"
              name="description"
              id="description"
              onChange={(e)=>{setCourse({...course, description: e.target.value})}}
              style={{ height: 150 }}
            ></Input>
          </FormGroup>
          <Container className="text-center">
            <Button type ="submit" color="success" style={{ width: 120 }} className="me-3">
              Add Course
            </Button>
            <Button color="danger" onClick={()=>{
              setCourse()
            }} style={{ width: 120 }}>
              Clear
            </Button>
          </Container>
        </Form>
      </Fragment>
    );
}

export default AddCourse;
