import React, { useState } from 'react'
import Course from './course'

function AllCourses(){
  const [course, setCourse] = useState([
    {
      title: "Java for Beginners",
      description:
        "This is an updated Java course for beginners with all the fundamental concepts",
    },
    {
      title: "React for Beginners",
      description:
        "This is an updated React course for beginners with all the fundamental javaScript concepts",
    },
    {
      title: "Django for All",
      description:
        "This is an updated Django course for beginners with all the fundamental concepts",
    },
  ]);

   return (
     <div className="text-cdenter">
       <h1>All Courses</h1>
       <p>List of all the courses available at affordable price</p>
       {
        course.length>0? course.map((item)=>(
          <Course course={item}/>
        )):"No course available"
       }
     </div>
   );
}

export default AllCourses;
