import { React,useState,useEffect } from 'react'
import axios from 'axios'
import { Button } from "reactstrap";
import { toast } from 'react-toastify'
import base_path from '../api/bootapi';

function DeleteCourse() {

    const deleteCourse =(name)=>{
        axios.delete(`${base_path}/courses/${name}`).then(
          (response) => {
            console.log("Success");
            toast.success("Course deleted successfully");
          
          },
          (error) => {
            console.log(error);
            toast.error("Error deleting course");
          }
        );
      }

    const [course, setCourse] = useState([]);

    const handleInputChange = (event) => {
        setCourse(event.target.value);
      };
    
    const updateCourses = (name)=>{
    setCourse(course.filter((c) => c.name != name));
  }
    return (
        <div className="flex flex-col mx-auto">
        <h2 className="mx-auto p-3">Delete the record</h2>
        <div className='flex flex-col bg-gray-100 border-1 p-3 border-black w-[300px] mx-auto '>
        <label className="mx-auto p-1 font-bold">Name: </label>
        <input type="text"   placeholder='Enter name (Case Sensitive)' className="mx-auto border-1 p-1 border-black w-[210px]" onChange={handleInputChange} />
        <Button type ="submit" onClick={deleteCours}  color="success"  className="mx-auto mt-2">
           Delete
        </Button>
        </div>
      {/* {rank != null && (
        <div className="flex justify-center mx-auto text-[20px] p-2 my-3 w-[200px] border-1 text-white border-black bg-green-600 ">
          { rank!=-1 ?  (
            <div className="">Your rank is {rank}</div>
          ) :(<div>Student Not found</div>)
          }
        </div>
      )} */}
    </div>
    )
}

export default DeleteCourse
