import React from 'react'
import { ListGroup } from 'reactstrap'
import { Link } from 'react-router-dom';

function Menu(){
  let previousButton = null;
  function changeColor(buttonId) {
    let button = document.getElementById(buttonId);
    let defaultButton = document.getElementById("myButton1");
    defaultButton.baseURI

    if (previousButton !== null) {
      previousButton.style.backgroundColor =
        "white"; /* Revert the previous button color to blue */
    }

    button.style.backgroundColor =
      "rgb(0, 102, 255)"; /* Change the color of the current button to red */
    previousButton = button;
  }
  
    return (
      <ListGroup>
        <Link id="myButton1" className="list-group-item list-group-item-action"tag="a" to="/" action>Home</Link>
        <Link id="myButton2" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor('myButton2')} to="/add-course" action>Add Course</Link>
        <Link id="myButton3" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor('myButton3')} to="/view-courses" action>View Courses</Link>
        <Link id="myButton4" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor('myButton4')} to="/about-us" action>About Us</Link>
        <Link id="myButton5" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor('myButton5')} to="/contact-us" action>Contact Us</Link>
      </ListGroup>
    ); 
}

export default Menu;
