import React from 'react'
import { ListGroup } from 'reactstrap'
import { Link } from 'react-router-dom';

function Menu(){
  var buttons = document.getElementsByClassName("color-change");

      function changeColor(button) {
        for (var i = 0; i < buttons.length; i++) {
          buttons[i].classList.remove("blue"); /* Remove 'blue' class from all buttons */
        }
        button.classList.add("blue"); /* Add 'blue' class to the clicked button */
      }

    return (
      <ListGroup>
        <Link class="color-change blue" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor(this)} to="/" action>Home</Link>
        <Link class="color-change" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor(this)} to="/add-course" action>Add Course</Link>
        <Link class="color-change" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor(this)} to="/view-courses" action>View Courses</Link>
        <Link class="color-change" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor(this)} to="/about-us" action>About Us</Link>
        <Link class="color-change" className="list-group-item list-group-item-action"tag="a" onClick={()=>changeColor('this')} to="/contact-us" action>Contact Us</Link>
      </ListGroup>
    ); 
}

export default Menu;
