import axios from 'axios';
import React from 'react'

import {
  Card,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  CardFooter,
  Button,
  Container,
} from "reactstrap";
import base_path from '../api/bootapi';
import { toast } from 'react-toastify';

function Course ({course,update}){

  const deleteCourse =(name)=>{
    axios.delete(`${base_path}/courses/${name}`).then(
      (response) => {
        console.log("Success");
        toast.success("Course deleted successfully");
        update(name)
      },
      (error) => {
        console.log(error);
        toast.error("Error deleting course");
      }
    );
  }
   
    return (
      <Card>
        <CardBody className="text-center">
          <CardSubtitle className="fw-bold p-2 ">Name: {course.name}</CardSubtitle>
          <CardText>Score: {course.score}</CardText>
          <CardText>City: {course.city}</CardText>
          <CardText>Country: {course.country}</CardText>
          <CardText>Pincode: {course.pincode}</CardText>
          <Container className="text-center">
            <Button color="danger" onClick={()=>{
              deleteCourse(course.name);
            }} className="me-3">Delete</Button>
            <Button color="primary">Update</Button>
          </Container>
        </CardBody>
      </Card>
    );
}

export default Course;
