import React, { useState } from 'react';
import base_path from '../api/bootapi';

import axios from 'axios';

const RankChecker = () => {
  const [name, setName] = useState('');
  const [rank, setRank] = useState(null);

  const handleInputChange = (event) => {
    setName(event.target.value);
  };

  const checkRank = () => {
    axios.get(`${base_path}/get-rank/${name}`)
      .then((response) => {
        setRank(response.data);
        console.log(name);
      },
      (error => {
        console.error(error);
        setRank(-1);
      })
      )
  };

  return (
    <div className="flex flex-col mx-auto">
      <h2 className="mx-auto p-3">Check Your Rank</h2>
      <div className='flex '>
        <label className="font-bold">Name: </label>
        <input type="text" value={name} className="mx-auto border-2 border-black" onChange={handleInputChange} />
        <button onClick={checkRank}>Check Rank</button>
      </div>
      {rank != null && (
        <div>
          { (
            <p>Your rank is {rank}</p>
          ) }
        </div>
      )}
    </div>
  );
};

export default RankChecker;
