import React, { useEffect, useState } from 'react'
import Course from './course'
import axios from 'axios'
import base_path from '../api/bootapi'
import {toast}  from 'react-toastify'

function AllCourses() {
  useEffect(() => {
    document.title = "All courses || Code Master";
  }, []);

  const getAllCoursesFromServer = () => {
    axios.get(`${base_path}/courses`).then(
      (response) => {
        console.log(response.data);
        toast.success("Success");
        setCourse(response.data);
      },
      (error) => {
        console.log(error);
      }
    );
  };

  useEffect(() => {
    getAllCoursesFromServer()
  },[]);

  const [course, setCourse] = useState([]);
  const updateCourses = (id)=>{
    setCourse(course.filter((c) => c.id != id));
  }

  return (
    <div className="text-center" style={{ width: "90%", marginLeft: "20%" }}>
      <h1 style={{ color: "white" }}>All Courses</h1>
      <p style={{ color: "white" }}>
        List of all the courses available at affordable price
      </p>
      {course.length > 0
        ? course.map((item) => (
            <div key={item.id} style={{ marginBottom: "10px" }}>
              <Course course={item} update ={updateCourses}/>
            </div>
          ))
        : "No course available"}
    </div>
  );
}

export default AllCourses;
